import React, { Component } from "react";
import {
  Modal,
  ModalBody,
  ModalFooter,
  ModalHeader,
  // Table,
  Button,
  // Badge,
  // Container,
  Row,
  Col,
  Input,
  Label,
  FormGroup,
  Form,
} from "reactstrap";
import { createAdmin } from "../http/http-calls";
// import config from '../config/index';
import { ToastsStore } from "react-toasts";
import { showToast } from "../helper-methods";
import { countryCodes } from "../config/country-codes";
import config from "../config";

class AddInfluencerModal extends Component {
  state = {
    influencer: {
      name: "",
      email: "",
      phone: "",
      countryCode: "+1",
      product: "",
      profileLink: "",
      // commission: "",
      sales: "",
      profilePicture: "",
    },
    errors: {},
    isDirty: {
      name: false,
      email: false,
      phone: false,
      profilePicture:false,
      profileLink:false,
    },
    loading: false,
  };

  _closeModal = () => {
    let { influencer, errors, isDirty } = this.state;
    influencer = {
      name: "",
      email: "",
      phone: "",
      countryCode: "+1",
    };
    isDirty = {
      name: false,
      email: false,
      phone: false,
    };
    errors = {};
    this.setState({ influencer, errors, isDirty }, () => {
      console.log(influencer, errors);
      this.props.toggle();
    });
  };

  _saveUser() {
    console.log("save user", this.state.influencer);
    this.setState({ loading: true });
    let data = {
      name: {
        first: this.state.influencer.name.split(" ")[0],
        last: this.state.influencer.name.split(" ")[1],
      },
      email: this.state.influencer.email,
      phone:
        "(" +
        this.state.influencer.countryCode +
        ")" +
        this.state.influencer.phone,
    };
    createAdmin(data).then(
      (response) => {
        console.log(response);
        this.setState({ loading: false });
        ToastsStore.success("Admin user added Successfully.", 3000);
        this.props.reloadAdminList();
        this._closeModal();
      },
      (error) => {
        console.log(error);
        // ToastsStore.error(error.reason, 3000);
        showToast(
          error.reason && error.reason.length
            ? error.reason
            : "Server error. Try again after sometime.",
          "error"
        );
        this.setState({ loading: false });
      }
    );
  }

  _handleOnChange = ({ currentTarget }) => {
    // console.log(currentTarget);
    const { influencer, isDirty } = this.state;
    influencer[currentTarget.name] = currentTarget.value;
    isDirty[currentTarget.name] = true;
    this.setState({ influencer });
    this._validateForm();
  };

  /**
