import React, { Component } from "react";
import {
  Container,
  Row,
  Col,
  Button,
  Input,
  FormGroup,
  Label,
  Form,
  InputGroup,         
  InputGroupAddon,
  InputGroupText,
  Card,
  CardBody
} from "reactstrap";
import { getSettings, updteSettings } from "../http/http-calls";
import { ToastsStore } from "react-toasts";
import Chips, { Chip } from "react-chips";
import { showToast } from "../helper-methods";
import CustomChip from "../chip";

const chipsTheme = {
  chipsContainer: {
    display: "flex",
    position: "relative",
    border: "1px solid #e4e7ea",
    backgroundColor: "#fff",
    fontSize: "13px",
    minHeight: 24,
    alignItems: "center",
    flexWrap: "wrap",
    padding: "3px 4px 4px",
    borderRadius: "4px",
    ":focus": {
      border: "1px solid #5c6873"
    }
  },
  container: {
    flex: 1
  },
  containerOpen: {},
  input: {
    border: "none",
